/**
 * Clone directory
 * @param dir Input Directory
 * @param out Output Directory
 * @param output Allow Output (console)
 */
export function clone(dir: string, out: string, output: boolean): void

/**
 * Check Directory
 * @param dir Directory
 */
export function isDirectory(dir: string): boolean

/**
 * Clone directory without Create Folder
 * @param dir 
 * @param out 
 * @param output 
 */
export function read(dir: string, out: string, output: boolean): void